import React, {Component} from 'react';
import { Media, Container, Col, Row, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { Link, withRouter } from 'react-router-dom';
import Linkify from 'react-linkify';
import axios from 'axios';
import createHistory from 'history/createBrowserHistory';

class CreateUser extends Component {

  constructor(props){
    super(props);
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.handleFirstNameChange = this.handleFirstNameChange.bind(this);
    this.handleLastNameChange = this.handleLastNameChange.bind(this);
    this.handleEmailChange = this.handleEmailChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);
 
    this.state = {
     firstName:'',
      lastName: '',
      email: '',
      password:'',
    };
  }
  
    handleFirstNameChange(event){
    this.setState({firstName: event.target.value});
  }
    handleLastNameChange(event){
    this.setState({lastName: event.target.value});
  }
    handleEmailChange(event){
    this.setState({email: event.target.value});
  }
    handlePasswordChange(event){
    this.setState({password: event.target.value});
  }
  
  
    onFormSubmit(event) {
    event.preventDefault();
    fetch('/account/',{
      method: 'POST',
      headers:{
        'Accept' : 'application/json',
        'Content-Type': 'application/json' 
      },
      body: JSON.stringify(this.state),
    });
    this.props.history.push('/')

    }

  
  render() {
    return(
       <Container>
        <center>
          <form onSubmit={this.onFormSubmit} class = 'createForm'> 
            <img width="100px" src={'https://scontent-lhr3-1.xx.fbcdn.net/v/t1.0-9/18622409_1370665929648398_390425705979425933_n.jpg?_nc_cat=101&_nc_ht=scontent-lhr3-1.xx&oh=f7b4cd27a0a17805194a7eaa54556aa9&oe=5C7C3796'} style={{marginBottom: '30px', borderRadius: '200px'}} className="img-responsive"/>

            <FormGroup>
              <h1 class="display-5">Create User</h1>
            </FormGroup>
            
            <FormGroup>
              <Input autoFocus type="text" name="firstName" id="examplefirstname" value = {this.state.firstName} placeholder="Forename" onChange ={this.handleFirstNameChange} required/>
            </FormGroup>

            <FormGroup>
              <Input type="text" name="lastName" id="examplelastname" value = {this.state.lastName} placeholder="Surname" onChange ={this.handleLastNameChange} required/>
            </FormGroup>
          
            <FormGroup>
              <Input type="email" name="email" id="exampleEmail"  value = {this.state.email} placeholder="Email" onChange ={this.handleEmailChange} required/>
            </FormGroup>

            <FormGroup>
              <Input type="password" name="password" id="examplePassword"  value = {this.state.password} placeholder="Password" onChange ={this.handlePasswordChange} required/>
            </FormGroup>

            <FormGroup>
              <Button type="submit" value="Create User" color="primary" size="me" block > Create User </Button>
            </FormGroup>
          </form>
        </center>
      </Container>
    )
  }
}
export default withRouter(CreateUser)
