import React from 'react';
import PDF from 'react-pdf-js';
import {Navbar, NavbarBrand, NavItem, NavLink, Col, Row, Container, Input, Table, ButtonGroup, Media, TabContent, TabPane, Nav, Card, Button, CardTitle, CardText,} from 'reactstrap';
 
class PDFComponent extends React.Component {
  state = {};
 
  onDocumentComplete = (pages) => {
    this.setState({ page: 1, pages });
  }
 
  handlePrevious = () => {
    this.setState({ page: this.state.page - 1 });
  }
 
  handleNext = () => {
    this.setState({ page: this.state.page + 1 });
  }
 
  renderPagination = (page, pages) => {
    let previousButton = <li style={{listStyleType: 'none', color: 'white'}} className="previous" onClick={this.handlePrevious}><a href="#"><i className="fa fa-arrow-left"></i> &larr;</a></li>;
    if (page === 1) {
      previousButton = <li style={{listStyleType: 'none'}} className="previous disabled"><a href="#"><i className="fa fa-arrow-left"></i> &larr;</a></li>;
    }
    let nextButton = <li style={{listStyleType: 'none'}} className="next" onClick={this.handleNext}><a href="#">&rarr; <i className="fa fa-arrow-right"></i></a></li>;
    if (page === pages) {
      nextButton = <li style={{listStyleType: 'none'}} className="next disabled"><a href="#">&rarr; <i className="fa fa-arrow-right"></i></a></li>;
    }
    return (
        <center>
            <ButtonGroup size="sm" className="pager">
                <Button color="primary">{previousButton}</Button>
                <Button color="primary">{nextButton}</Button>
            </ButtonGroup>
        </center>
    //   <nav>
    //     <ul className="pager">
    //       {previousButton}
    //       {nextButton}
    //     </ul>
    //   </nav>
      );
  }
 
  render() {
    let pagination = null;
    if (this.state.pages) {
      pagination = this.renderPagination(this.state.page, this.state.pages);
    }
    return (
      <div>
        {pagination}
        <PDF
            file="DanHiggins.pdf"
            onDocumentComplete={this.onDocumentComplete}
            page={this.state.page}
            />
      </div>
    )
  }
}
 
export default PDFComponent;
