import React, {Component} from 'react';
import {Navbar, NavbarBrand, NavItem, NavLink, Col, Row, Container, Input, Table, ButtonGroup, Media, TabContent, TabPane, Nav, Card, Button, CardTitle, CardText,} from 'reactstrap';
import Linkify from 'react-linkify';
import classnames from 'classnames';
import GoogleMapReact from 'google-map-react'

import PDF from 'react-pdf-js';

const AnyReactComponent = ({ text }) => <div>{ text }</div>

export default class MainPage extends Component {
    static defaultProps = {
        center: { lat: 53.474192, lng: -2.286306 },
        zoom: 11
    }

    state = {};
 
    onDocumentComplete = (pages) => {
        this.setState({ page: 1, pages });
    }
    
    handlePrevious = () => {
        this.setState({ page: this.state.page - 1 });
    }
    
    handleNext = () => {
        this.setState({ page: this.state.page + 1 });
    }
    
    renderPagination = (page, pages) => {
        let previousButton = <li className="previous" onClick={this.handlePrevious}><a href="#"><i className="fa fa-arrow-left"></i> Previous</a></li>;
        if (page === 1) {
        previousButton = <li className="previous disabled"><a href="#"><i className="fa fa-arrow-left"></i> Previous</a></li>;
        }
        let nextButton = <li className="next" onClick={this.handleNext}><a href="#">Next <i className="fa fa-arrow-right"></i></a></li>;
        if (page === pages) {
        nextButton = <li className="next disabled"><a href="#">Next <i className="fa fa-arrow-right"></i></a></li>;
        }
        return (
        <nav>
            <ul className="pager">
            {previousButton}
            {nextButton}
            </ul>
        </nav>
        );
    }
    
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
            activeTab: '1'
        };
    }

    toggle(tab) {
        if (this.state.activeTab !== tab) {
            this.setState({
                activeTab: tab
            });
        }
    }


    render() {
         let pagination = null;
        if (this.state.pages) {
        pagination = this.renderPagination(this.state.page, this.state.pages);
        }
        return (
            <div>
                <Navbar color="light" light expand="md">
                    <NavbarBrand style={{marginLeft: '10px'}} class="logo" href="#">CV Management</NavbarBrand>
                    <Nav className="ml-auto" navbar>
                        <NavItem>
                            <NavLink href="#">Profile</NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href="#">Logout</NavLink>
                        </NavItem>
                    </Nav>
                </Navbar>

                <center>
                    <h1 style={{marginTop: '40px'}}>Profile</h1>
                </center>

                <Container name="mainPage" style={{backgroundColor: 'white', minHeight: '700px', marginTop: '30px', boxShadow: '5px 5px #c9cacc'}}>  
                    <Row>
                        <Col xs="6">
                            <Row>
                                <Col xs="3" xs="6">
                                    <img width="250px" src={'https://bit.ly/2Fvw9xi'} style={{paddingTop: '50px', paddingLeft: '50px'}} className="img-responsive"/>
                                </Col>
                                <Col xs="3" xs="6">
                                    <h6 style={{paddingTop: '50px', color: '#888a8c'}}>Name</h6>
                                    <div style={{width: '200px', height: '1px', backgroundColor: 'grey'}}></div>
                                    <h5 style={{paddingTop: '10px', color: '#636466'}}>Aharan Manoharan</h5>
                                    <h6 style={{paddingTop: '10px', color: '#888a8c'}}>Email</h6>
                                    <div style={{width: '200px', height: '1px', backgroundColor: 'grey'}}></div>
                                    <h5 style={{paddingTop: '10px', color: '#636466'}}>testmail@gmail.com</h5>
                                </Col>
                            </Row>
                            <center>
                                <div style={{height: '350px', width: '82%', paddingTop: '30px'}}>
                                    <GoogleMapReact
                                        bootstrapURLKeys={{ key: ''}}
                                        defaultCenter={this.props.center}
                                        defaultZoom={this.props.zoom}
                                    >
                                    <AnyReactComponent
                                        lat={53.474192}
                                        lng={-2.286306}
                                        text={'Manchester'}
                                    />
                                    </GoogleMapReact>
                                </div>
                            </center>
                            <br/>
                            <Linkify><a style={{paddingLeft: '50px'}} href='#'>Edit account</a></Linkify>
                        </Col>
                        <Col xs="6">
                            <Nav tabs style={{marginTop: '10px'}}>
                                <NavItem>
                                    <NavLink className={classnames({ active: this.state.activeTab === '1' })} onClick={() => { this.toggle('1'); }}>
                                        CV 1
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className={classnames({ active: this.state.activeTab === '2' })} onClick={() => { this.toggle('2'); }}>
                                        CV 2
                                    </NavLink>
                                </NavItem>
                            </Nav>
                            <TabContent activeTab={this.state.activeTab}>
                                <TabPane tabId="1">
                                    <Row>
                                    <Col sm="12">
                                        <div>
        <PDF
          file="test.pdf"
          onDocumentComplete={this.onDocumentComplete}
          page={this.state.page}
        />
        {pagination}
      </div>
                                         <img width="80%" style={{marginTop: '40px'}} src={'https://db-cdn.imgix.net/layout/image/Wl2rYyJ34r/4/screen.png'} className="img-responsive"/> 
                                    </Col>
                                    </Row>
                                </TabPane>
                                <TabPane tabId="2">
                                    <Row>
                                    <Col sm="12">
                                         <img width="80%" style={{marginTop: '40px'}} src={'https://www.seoclerk.com/pics/338932-1Nmzpp1425821886.jpg'} className="img-responsive"/>
                                    </Col>
                                    </Row>
                                </TabPane>
                            </TabContent>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}
